INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Error Reproduction & Isolation', 'Diagnose an intermittent segfault in a C utility that only occurs when compiled with -O2. Reproduce by varying compiler flags and inputs, then reduce the program to a minimal C file that crashes reliably and reveals the underlying undefined behavior.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Error Reproduction & Isolation', 'Reproduce a flaky ''Address already in use'' failure by isolating the smallest script that deterministically triggers a TCP port reuse race (TIME_WAIT) during rapid server restarts. Build a tiny server/client and use only standard OS tools to narrow the failure to missing socket cleanup/options, yielding a minimal reproducible case.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Error Reproduction & Isolation', 'Reproduce an intermittent deadlock in a Go HTTP server that occurs only under specific GOMAXPROCS values and concurrent request patterns, then isolate a minimal single-file program and deterministic request sequence that triggers it. Capture the exact environment and commands used (race detector, pprof blocking profile, goroutine dumps) to demonstrate the deadlock reliably.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Error Reproduction & Isolation', 'Reproduce an intermittent timestamp parsing failure that occurs only during daylight-saving transitions by controlling TZ and the system clock inside the sandbox. Isolate the minimal failing input and code path by iterating across DST boundary timestamps and documenting the exact timezone/offset combination that triggers the error.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Error Reproduction & Isolation', 'Reproduce and isolate a CLI tool crash that emits ''Broken pipe'' or exits non-zero when its stdout is piped to head/grep -m1, identifying the minimal code path that triggers SIGPIPE/EPIPE under controlled conditions. Implement graceful handling so the pipeline exits cleanly without stack traces or spurious errors.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Postmortem Documentation & Preventive Measures', 'Investigate a midnight outage where Nginx served 502s after a certbot renewal triggered an automatic reload that pulled in an incomplete vhost via a wildcard include. Diagnose configs/logs, implement a safe reload gate (nginx -t and atomic writes), and produce a postmortem detailing root cause, impact, and preventive measures.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Postmortem Documentation & Preventive Measures', 'Investigate a production failure where package installs and writes error with ''No space left on device'' despite ample free disk space, trace the root cause to inode exhaustion from a runaway temp/log file generator, and validate via df -i and remediation. Produce a concise postmortem detailing impact, timeline, contributing factors, and preventive measures such as file-count monitoring, log rotation policies, tmp cleanup jobs, and quotas.', NULL, ARRAY['monitoring']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Postmortem Documentation & Preventive Measures', 'Investigate an incident where API requests intermittently returned 401 due to container clock drift causing JWT nbf/exp validation failures around a DST transition. Reconstruct the timeline from logs and metrics, identify the root cause, and produce a postmortem summarizing impact, detection, and preventive measures (time synchronization, validation leeway, targeted monitoring/alerts).', NULL, ARRAY['api', 'container', 'monitoring']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Postmortem Documentation & Preventive Measures', 'Investigate intermittent authentication failures (401/403) traced to JWT validation errors caused by container clock drift and misconfigured timezone/time sync. Write a postmortem detailing timeline, root cause, impact, detection gaps, and concrete preventive measures (time synchronization policy, startup time sanity checks, monitoring/alerts, and validation tolerances).', NULL, ARRAY['container', 'monitoring']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Postmortem Documentation & Preventive Measures', 'Investigate why a nightly data export executed twice and produced corrupted partial uploads by reconstructing the timeline from cron logs, application logs, and a stale PID lock during a DST transition. Write a postmortem detailing root cause, impact scope, contributing factors, and preventive measures (timezone policy, idempotent runs, and robust lock hygiene).', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Traceback & Stack Analysis', 'Diagnose a Java service hang by capturing and analyzing multiple jstack thread dumps to trace a lock-order inversion deadlock (each thread waiting on the other’s monitor) and identify the exact code paths involved. Implement a fix by enforcing a consistent lock acquisition order or using tryLock with timeouts, rebuild, and verify the service remains responsive under concurrent load.', NULL, ARRAY['java']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Traceback & Stack Analysis', 'Investigate a Java service that fails at startup with a NoSuchMethodError originating from org.apache.commons.lang3 in the stack trace, indicating JAR hell from conflicting transitive dependencies. Use gradle dependencies to trace the classpath, add dependency constraints or exclusions to resolve the conflict, then rebuild and verify the service starts and a provided smoke test passes.', NULL, ARRAY['java']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Traceback & Stack Analysis', 'Investigate a Node.js CLI that crashes on startup with "RangeError: Maximum call stack size exceeded"; run with --trace-uncaught and --trace-warnings to obtain full stack traces and pinpoint a circular require/import loop causing infinite recursion. Refactor the modules to break the cycle and verify the program starts and returns the expected output.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Traceback & Stack Analysis', 'Investigate a Python ProcessPool-based task that fails with BrokenProcessPool by reading the exception chain and worker stack traces to find the underlying pickling error caused by a nested/local function. Refactor the callable to be picklable (e.g., move to module scope) and verify the parallel job completes successfully.', NULL, ARRAY['python', 'parallel']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Root Cause Analysis & Postmortem Debugging', 'Traceback & Stack Analysis', 'Investigate an intermittently crashing Go HTTP service by analyzing its panic log and full goroutine dump to trace the execution path to a ''concurrent map writes'' failure and pinpoint the offending map and call sites. Implement a minimal synchronization fix and a stress test that runs with -race to verify the panic no longer occurs.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Access Policy & Role Misconfiguration', 'Diagnose and correct a MinIO S3 setup where a data-ingest user cannot list or upload objects due to conflicting bucket and user policies. Apply the least-privilege fix by adjusting the bucket and user policies so that aws s3 ls and multipart uploads succeed only within the intended bucket/prefix.', NULL, ARRAY['aws']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Access Policy & Role Misconfiguration', 'Diagnose and fix a Redis ACL misconfiguration that blocks a non-default user from executing required commands (e.g., SET/GET) on a specific key prefix used by a sample app. Update the ACLs to grant least-privilege access for that user and verify the app completes successfully end-to-end.', NULL, ARRAY['redis']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Access Policy & Role Misconfiguration', 'Diagnose and repair a Kubernetes RBAC misconfiguration that prevents a CI service account from applying resources in a specific namespace. Adjust Roles/ClusterRoles and the corresponding (Cluster)RoleBindings to grant the minimal verbs and scopes required so a provided kustomize/helm deploy completes successfully using only that service account.', NULL, ARRAY['kubernetes']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Access Policy & Role Misconfiguration', 'Diagnose why a data-ingestion script receives 403 AccessDenied when uploading to an S3-compatible MinIO bucket due to a mismatched bucket policy and user credentials. Correct the bucket and user policies to grant least-privilege PutObject/ListBucket on the intended prefix, update the client configuration, and verify the script completes successfully with upload and listing.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Access Policy & Role Misconfiguration', 'Fix a Kubernetes RBAC setup where the ci-deployer ServiceAccount cannot create or patch Deployments in the staging namespace, yet is mistakenly allowed to read Secrets cluster-wide. Diagnose Role/ClusterRole and (Cluster)RoleBinding scope and apiGroup/verb mismatches, then rework the policy to grant least-privilege deploy rights only in staging and no Secrets access; verify with kubectl auth can-i and by successfully applying the provided manifest.', NULL, ARRAY['kubernetes']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Authentication & Authorization Failures', 'Debug an AWS CLI profile that intermittently fails to access an S3 bucket via SSO + AssumeRole (AccessDenied and ExpiredToken errors). Refresh the SSO cache, correct the profile’s role_arn/region, and ensure the role has s3:ListBucket/s3:GetObject/s3:PutObject permissions; verify by listing the bucket and uploading a test object.', NULL, ARRAY['aws']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Authentication & Authorization Failures', 'Diagnose and fix a local PostgreSQL login failure for user ''app'' where URI-based connections are rejected due to pg_hba.conf rule order, SCRAM-vs-MD5 password mismatch, and missing LOGIN/CONNECT privileges. Correct the configuration and role settings, reload PostgreSQL, and verify with psql postgresql://app:app@localhost:5432/appdb -c SELECT 1.', NULL, ARRAY['postgresql']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Authentication & Authorization Failures', 'Diagnose and repair a local PostgreSQL connection that fails with pg_hba.conf rejection and mismatched password method (SCRAM vs md5), preventing a bundled Python app from logging in. Update pg_hba.conf, recreate the user with the correct authentication scheme and role grants, reload Postgres, and verify a psycopg2 script can perform read/write queries.', NULL, ARRAY['postgresql', 'python', 'logging']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Authentication & Authorization Failures', 'Diagnose and resolve npm 401 Unauthorized errors when installing from a local Verdaccio registry by pinpointing bad/expired credentials or scope misconfiguration in ~/.npmrc and registry ACLs. Reauthenticate or correct token scopes/registry settings so installing a private package succeeds from the terminal.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Authentication & Authorization Failures', 'Diagnose why kubectl returns Forbidden and token-related errors using the provided kubeconfig for namespace analytics. Replace the expired/invalid ServiceAccount token and create the minimal Role/RoleBinding needed, update kubeconfig, and verify by successfully applying manifests and listing pods in that namespace.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Environment Secrets & Credential Mismanagement', 'Diagnose a bootstrap script that fails to fetch secrets from a local HashiCorp Vault due to a stale VAULT_TOKEN, missing VAULT_NAMESPACE, and incorrect CA settings. Implement AppRole login using role_id/secret_id files, set VAULT_ADDR/VAULT_CACERT/VAULT_NAMESPACE correctly, and update the script so non-interactive secret retrieval and app startup succeed.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Environment Secrets & Credential Mismanagement', 'Diagnose and fix a CI/container build that fails to fetch private npm packages and Git submodules due to mismanaged tokens (missing/misnamed env vars, malformed .npmrc, and SSH key permission issues). Repair the credential chain and config so npm install and git submodule update complete non-interactively.', NULL, ARRAY['container', 'git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Environment Secrets & Credential Mismanagement', 'Diagnose and fix a Dockerized Node.js build that fails npm install with 401 due to a missing or mis-scoped NPM_TOKEN for a private registry. Modify the Dockerfile and build command to securely pass the token only to the dependency layer and verify by successfully building and running the app that imports the private package.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Environment Secrets & Credential Mismanagement', 'Diagnose and repair a failing S3 backup in a local MinIO sandbox caused by mismanaged AWS credentials and environment (incorrect AWS_ACCESS_KEY_ID/SECRET, missing region, wrong endpoint URL, and a conflicting default profile). Reconfigure environment variables and ~/.aws files so both aws CLI and a boto3 script can authenticate and list the target bucket without hardcoding secrets.', 'hard', ARRAY['aws']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'Environment Secrets & Credential Mismanagement', 'Diagnose why sops cannot decrypt secrets.enc.yaml in the container because the AGE private key is provided as a base64, single-line environment variable that is never materialized as a key file. Reconstruct the PEM from the env var, set SOPS_AGE_KEY_FILE with correct permissions, and verify decryption by producing /app/secrets.yaml.', NULL, ARRAY['container']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'File & Directory Permission Errors', 'A reverse proxy cannot connect to its backend via a Unix domain socket at /run/app.sock due to permission denied on the socket path. Diagnose and correct directory/socket ownership, mode bits, and group/ACL settings so the proxy user can connect, then verify a 200 OK from curl localhost.', NULL, ARRAY['backend']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'File & Directory Permission Errors', 'Diagnose and fix a Unix domain socket connection failing with ''Permission denied'' by auditing the socket file and its parent directory ownership/modes and the creator’s umask. Implement a least-privilege solution (e.g., dedicated group, setgid directory, corrected socket permissions), then verify the client can connect and exchange data successfully.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'File & Directory Permission Errors', 'Diagnose and fix a reverse proxy failing with 502 because nginx cannot access a backend UNIX domain socket (EACCES) due to incorrect ownership/permissions on the socket and its parent directories. Adjust ownership, mode, and directory execute bits or set ACLs, then make the fix persistent by updating the systemd .socket/.service or tmpfiles.d configuration, and verify that curl via nginx returns 200.', NULL, ARRAY['backend']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'File & Directory Permission Errors', 'Diagnose why a client fails to connect to a local service over a Unix domain socket (/run/app/app.sock) with EACCES and fix the underlying directory and socket ownership/permission and group-membership issues. Verify the repair by enabling the non-root client to connect end-to-end using the provided health-check script.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Security & Access Debugging', 'File & Directory Permission Errors', 'Diagnose why a client receives ''Permission denied'' when connecting to a Unix domain socket at /app/run/metrics.sock: the socket’s parent directory lacks execute permission for the client’s group and the daemon’s umask creates overly restrictive socket permissions. Implement a least-privilege fix by adjusting directory ownership/modes and the daemon’s umask or group, then verify bidirectional communication over the socket.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Background Service Failures', 'Diagnose a systemd socket-activated service that never starts on client connect due to mismatched unit names, incorrect ListenStream/ListenUnix path, and restrictive socket directory permissions. Correct the .socket/.service units (naming, ExecStart, WorkingDirectory, User), reload and enable socket activation, and verify the daemon spawns on demand and successfully serves a test request.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Background Service Failures', 'Investigate a failing systemd timer + service that should create periodic backups but exits immediately under systemd while the same script runs fine interactively. Diagnose and fix unit misconfigurations (absolute ExecStart, WorkingDirectory, EnvironmentFile, and executable permissions), reload the daemon, and verify the timer produces a backup in /app/backups.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Background Service Failures', 'Investigate a systemd-managed Celery worker that immediately enters a restart loop because it launches before its Redis backend and points to a non-existent virtualenv, producing empty or truncated logs. Repair the unit by correcting ExecStart/WorkingDirectory/Environment, adding Requires= and After= redis-server.service, and verifying the service stays active and processes a sample task.', NULL, ARRAY['redis', 'backend']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Background Service Failures', 'Investigate a systemd-managed daemon that immediately enters a restart loop because unit hardening (DynamicUser with ProtectSystem=full) prevents creation of its PID/log/state files. Use journalctl and unit inspection to diagnose and then update the unit to provision writable runtime/state paths (e.g., RuntimeDirectory/StateDirectory or ReadWritePaths) so the service starts and stays active, verified by a heartbeat file appearing.', 'hard', NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Background Service Failures', 'Investigate why a systemd timer’s backup.service succeeds when started manually but fails when triggered by the timer due to environment/working-directory differences and reliance on relative paths. Update the unit(s) to use absolute ExecStart, set WorkingDirectory and Environment (PATH/HOME), add logging, then verify the timer fires and completes successfully.', NULL, ARRAY['logging']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Log Inspection & Anomaly Detection', 'Analyze journald, kernel dmesg, and application/service logs to time-correlate intermittent 5xx outages with OOM-killer events caused by a runaway process triggered by a cron job. Produce a concise root-cause timeline and propose a mitigation (e.g., systemd MemoryMax) to prevent recurrence.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Log Inspection & Anomaly Detection', 'Correlate a recurring 2–3 minute outage by analyzing nginx access/error logs, systemd-journal logs, and cron logs while compensating for a deliberate 5‑minute clock skew between components. Identify that a misconfigured logrotate postrotate script intermittently sends SIGSTOP to the web app and apply a minimal fix so the outage ceases.', NULL, ARRAY['web']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Log Inspection & Anomaly Detection', 'Diagnose intermittent restarts of a systemd-managed service by analyzing journalctl and kernel logs to detect OOM-killer and memory pressure events time-correlated with the service’s crashes. Implement a mitigation (e.g., adjust MemoryMax or service configuration) and verify stable operation with no recurring log anomalies.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Log Inspection & Anomaly Detection', 'Investigate intermittent TLS handshake failures by scanning journalctl/syslog and application logs for anomalies like “certificate not yet valid” alongside abrupt timestamp jumps, correlating them with NTP desynchronization events. Restore correct time sync (e.g., via systemd-timesyncd or chrony) and verify by re-running a TLS client to confirm error-free handshakes and aligned log timestamps.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Log Inspection & Anomaly Detection', 'Scan syslog, auth.log, and NTP logs (including rotated .gz) to detect a surge in authentication errors and correlate it with a significant NTP time step, pinpointing the drift magnitude and window. Output a concise timeline report naming the triggering event and the impacted services (e.g., sshd, sudo) where errors align with the clock change.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Process Crashes & Core Dumps', 'A Node.js script using the sharp image library crashes with SIGSEGV; enable core dumps and use coredumpctl + gdb (with addr2line) to analyze the core and trace the fault into libvips due to an ABI/version mismatch. Repair by installing compatible libvips/sharp binaries or rebuilding the module, then verify an image resize completes without crashing.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Process Crashes & Core Dumps', 'Analyze a core dump from a Rust CLI that intermittently SIGSEGVs during FFI calls into a C library. Use gdb and addr2line to pinpoint a dangling pointer/use-after-free across the FFI boundary, patch the Rust/C bindings to fix ownership and lifetimes, rebuild, and verify the crash no longer occurs under the stress script.', NULL, ARRAY['rust']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Process Crashes & Core Dumps', 'Diagnose why /app/bin/ingest crashes with SIGSEGV and leaves /app/core by using gdb to obtain a fully symbolicated backtrace (installing debug symbols if needed) and tracing the fault to a NULL function pointer returned by dlsym due to a missing plugin symbol. Repair the runtime environment (e.g., install the correct .so or adjust LD_LIBRARY_PATH) or patch the loader to handle NULL safely, and verify the program runs to completion without crashing.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Process Crashes & Core Dumps', 'Investigate a Node.js service that segfaults when requiring a native addon, analyzing the generated core dump with gdb to pinpoint an ABI/version mismatch between Node and the .node binary. Rebuild the addon for the correct Node-API/ABI (e.g., via node-gyp or prebuilds), replace the artifact, and verify the service runs and responds to a simple request without crashing.', NULL, ARRAY['api']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'Process Crashes & Core Dumps', 'Reproduce and analyze a core dump from a multithreaded C service that intermittently segfaults on SIGTERM due to a reentrant, non-async-signal-safe signal handler freeing shared memory twice. Enable core dumps, use gdb to pinpoint the crashing thread and double-free path, refactor the handler to defer work via a self-pipe and enforce single-ownership, rebuild, and verify stability under a stress harness.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'System Resource Exhaustion', 'Diagnose a backup job that aborts with "No space left on device" even though df shows ample free space, uncovering inode exhaustion from millions of stale temp/log files. Mitigate by reclaiming inodes and preventing recurrence (e.g., targeted cleanup, log rotation, and moving TMPDIR to a larger filesystem), then rerun the backup to complete successfully.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'System Resource Exhaustion', 'Diagnose intermittent ''EADDRNOTAVAIL'' and ''cannot assign requested address'' errors in a service that spawns many short-lived outbound TCP connections, tracing the issue to ephemeral port exhaustion and sockets stuck in TIME_WAIT. Implement mitigations (e.g., enable connection pooling, tune the ephemeral port range and TIME_WAIT reuse via sysctl in the container), then rerun load to verify stable connectivity.', NULL, ARRAY['container']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'System Resource Exhaustion', 'Diagnose intermittent cargo build/test failures in a Rust workspace that end with ''Killed'' or LLVM crashes, caused by OOM from parallel compilation and a nearly full disk from stale build artifacts. Mitigate by freeing disk space, adding a swapfile, and limiting build parallelism, then rebuild and run the tests to confirm stability.', NULL, ARRAY['rust', 'parallel', 'compilation']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'System Resource Exhaustion', 'Diagnose sporadic ''No space left on device'' errors despite ample free disk by identifying inode exhaustion caused by millions of small files in /var/tmp/app-cache. Implement cleanup and a prevention policy (e.g., tmpfiles.d or a cleanup cron) and verify new files can be created successfully.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'System & Process Diagnostics', 'System Resource Exhaustion', 'Diagnose why package installs and temporary builds fail with ''No space left on device'' by pinpointing which filesystem is full (/var or /tmp) and identifying the largest consumers (journald logs, apt/pip caches, orphaned artifacts). Reclaim space and implement mitigations (logrotate/journald limits, apt clean, TMPDIR relocation) so subsequent installs and builds succeed reliably.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Git & Version Control Issues', 'Diagnose and fix a Git repository that refuses to push due to a detached HEAD, a missing refs/heads/main, and an ''origin'' remote pointing at a non-bare working tree. Reconstruct the branch from reflog/lost-found, restore refs, convert or replace the remote with a bare repo, and verify new commits can be pushed and fetched cleanly.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Git & Version Control Issues', 'Diagnose and repair a Git repo where refs are lost and the repository is stuck mid-rebase: HEAD is detached, refs/heads/main is missing due to a corrupted packed-refs, and an index.lock prevents most commands. Recover the branch from reflog and dangling commits, clean up the rebase/lock state, and complete a fast-forward push to origin/main.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Git & Version Control Issues', 'Diagnose and repair a repository with broken Git submodules where .gitmodules contains incorrect paths/URLs and the submodule SHAs are orphaned, causing clone/update to fail. Fix the .gitmodules entries, sync configs, repoint submodules to valid remotes and commits, reinitialize recursively, and verify a fresh clone yields a clean working tree with all submodules checked out.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Git & Version Control Issues', 'Investigate a repository that fails to clone recursively because a submodule references a non-existent commit and an outdated remote. Fix by updating .gitmodules and the submodule’s origin URL, rewriting the parent commit to point at a reachable submodule revision if necessary, and verify that a fresh --recursive clone completes with a clean working tree.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Git & Version Control Issues', 'Repair a Git repository that fails with ''fatal: bad object HEAD'' due to a corrupted packed-refs file and a missing refs/heads directory. Reconstruct branch and tag refs from reflogs and reachable commits, restore HEAD to main, verify with git fsck, and push the recovered history to a new local bare remote.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Local vs Remote Environment Divergence', 'Diagnose a CI build that fails because large binary assets are missing while the same repo builds locally, uncovering that Git LFS objects are not fetched in the remote environment. Install and configure git-lfs, update the checkout/build steps to fetch and checkout LFS files, and verify the build completes with the required assets present.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Local vs Remote Environment Divergence', 'Diagnose a Node.js/TypeScript project that passes locally but fails in CI because CI uses NODE_ENV=production and npm ci --omit=dev, leaving runtime modules incorrectly placed in devDependencies unavailable. Reproduce both environments and then fix the workflow by pinning Node and reclassifying dependencies/build steps so the full test suite passes identically in both.', NULL, ARRAY['typescript']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Local vs Remote Environment Divergence', 'Diagnose why a Python test suite passes locally but fails in the provided CI container due to differing locale and timezone settings (e.g., C vs en_US.UTF-8, UTC vs local), leading to UnicodeDecodeError and timestamp assertion mismatches. Standardize encoding and timezone across environments via environment variables and test/config changes so pytest passes identically in both contexts.', NULL, ARRAY['python', 'container']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Local vs Remote Environment Divergence', 'Investigate Python tests that pass locally but fail in the provided CI-like container due to differences in locale and timezone affecting sorting, casing, and datetime parsing. Identify the mismatch (e.g., POSIX/C vs UTF-8 locale and non-UTC TZ) and reconcile it by standardizing environment settings and hardening code/tests to be locale- and TZ-independent.', 'hard', ARRAY['python', 'container']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Local vs Remote Environment Divergence', 'Investigate a Python project whose tests pass locally but fail in the provided CI container due to differing default locale (C/POSIX vs en_US.UTF-8) and timezone (UTC vs local), affecting collation and datetime formatting. Make the test run deterministic by enforcing explicit locale/TZ and locale-independent sorting/parsing so the suite passes in both environments.', NULL, ARRAY['python', 'container']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Testing & CI/CD Failures', 'Diagnose a Jest/TypeScript test suite that passes locally but fails in CI due to case-sensitive path resolution on Linux (imports reference files with incorrect casing). Correct the import paths and adjust TypeScript/Jest module resolution to enforce strict casing, add a CI check to catch casing mismatches, and verify the pipeline passes.', NULL, ARRAY['typescript']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Testing & CI/CD Failures', 'Diagnose why a pytest suite passes locally but fails in CI where LANG=C and TZ=UTC cause Unicode encoding and time-format assertions to break. Make the pipeline and/or code locale- and timezone-stable (install a UTF-8 locale, set LANG/LC_ALL/TZ, and remove locale-dependent assumptions) and demonstrate a consistent green run.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Testing & CI/CD Failures', 'Investigate a Python pytest suite that passes locally but fails in CI due to hidden timezone/locale assumptions (naive datetime comparisons and locale-dependent formatting). Make tests deterministic by enforcing UTC and a fixed locale in both code and pipeline (e.g., TZ=UTC, LC_ALL=C) and refactoring tests to use timezone-aware datetimes or freezegun, then verify consistent green runs.', NULL, ARRAY['python']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Testing & CI/CD Failures', 'Investigate why tests that rely on large sample assets fail in CI but pass locally, discovering that Git LFS-tracked files are checked out as pointer stubs in the CI environment. Initialize and configure Git LFS in the pipeline, fetch actual binaries, and rerun the suite until all tests pass.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Testing & CI/CD Failures', 'Reproduce and fix a CI-only failure in a React/Jest snapshot test that passes locally but fails in a headless CI container due to missing system fonts and locale/timezone differences. Identify the mismatches, install/configure the necessary fonts, set deterministic TZ/LC_* and rendering flags, and make the pipeline pass with consistent snapshots.', NULL, ARRAY['container']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Tool Misconfiguration & Dependency Drift', 'Diagnose a JavaScript monorepo where CI fails during yarn install due to a Yarn v1/v4 mismatch and missing Berry metadata (lockfile format drift, absent .yarn/releases, and no packageManager field), causing workspace binaries to be unavailable. Repair by aligning the Yarn version via Corepack, restoring .yarnrc.yml and releases, regenerating a compatible lockfile, and verify by running yarn install and yarn workspaces run test successfully.', NULL, ARRAY['javascript']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Tool Misconfiguration & Dependency Drift', 'Diagnose a Node.js monorepo where CI fails because the pipeline runs npm ci against a pnpm-managed workspace using pnpm-lock.yaml, breaking workspace links and dependency resolution. Reconfigure the workflow to use a pinned pnpm via corepack, install with pnpm in all jobs, regenerate the lockfile if needed, and verify that build and tests complete successfully.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Tool Misconfiguration & Dependency Drift', 'Diagnose a Rust workspace that fails in a simulated CI script due to dependency drift and toolchain mismatch (yanked versions in Cargo.lock, incompatible feature flags, and an unsupported rustc). Repair by pinning a compatible toolchain, updating/patching dependencies and regenerating the lockfile so cargo build and cargo test complete successfully.', NULL, ARRAY['rust']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Tool Misconfiguration & Dependency Drift', 'Diagnose and repair a broken Git pre-commit setup where hooks (e.g., black/isort/flake8/mypy) fail due to Python interpreter mismatches and outdated hook revisions. Update .pre-commit-config.yaml and environment so pre-commit install and pre-commit run -a complete successfully and reproducibly from the terminal.', NULL, ARRAY['git', 'python']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Debugging & Troubleshooting', 'Toolchain & Workflow Diagnostics', 'Tool Misconfiguration & Dependency Drift', 'Fix a failing Node.js monorepo build/test workflow that uses pnpm and corepack: the CI script selects an incompatible Node version, ignores the repo’s pinned pnpm, and relies on a stale lockfile that breaks workspaces. Update the workflow and configs (e.g., .nvmrc/engines, corepack enablement, pnpm-lock.yaml/cache keys) so the provided run-ci.sh passes on a clean checkout.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Command Discovery Quests', 'Embark on a Manual Page Scavenger Hunt where each stage hides a token inside the EXAMPLES, ENVIRONMENT, or SEE ALSO sections of specific man/info/tldr entries discovered via apropos and --help. Collect all tokens and use the newly learned subcommands and rarely used flags to build a validating command pipeline that outputs the final passphrase to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Command Discovery Quests', 'Explore a custom git-style CLI called cmdschool whose subcommands are intentionally undocumented except via --help, man pages, apropos, and an interactive cmdschool tutor, and learn to combine preinstalled tools (jq, ripgrep, curl, gpg, imagemagick) to pass five stages. Finish when cmdschool status reports ''graduated'' and write the issued certificate token to /app/results.txt.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Command Discovery Quests', 'Explore a custom lab CLI that discovers subcommands from executables named ''lab-*'' on the PATH; by chaining ''lab help'' and each subcommand''s --help/man pages, deduce the correct workflow to authenticate and extract a hidden secret. Write the recovered secret to /app/flag.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Command Discovery Quests', 'Explore a sandbox full of custom, undocumented CLI tools in /app/tools and hinted only via man/--help to discover how to reconstruct a corrupted, sharded archive. Infer and execute the correct pipeline (index, stitch, parity-fix, decode, decompress, verify) to recover the plaintext and write it to /app/answer.txt.', 'hard', NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Command Discovery Quests', 'Explore a sandbox of unknown CLI tools with self-documented interfaces (e.g., --help, help subcommands, and man pages) to learn their capabilities and compose them into a working pipeline. Use the discovered commands to decode a multipart artifact and write the exact recovered message to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Error Interpretation & Correction Games', 'Broken Build Bootcamp: a staged C project with intentional faults (misconfigured Makefile flags, segfaults, and undefined behavior) driven by a tutor CLI that provides compiler/linker logs, sanitizer traces, and unit-test feedback. Progress by interpreting each failure and patching the Makefile and C sources until every stage reports PASS.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Error Interpretation & Correction Games', 'Interact with a Shell Clinic tutor that presents five stages of broken Bash scripts; running clinic stageN executes tests, shows minimal logs, and offers targeted hints after each failure. Diagnose and fix quoting, word-splitting, pipefail, subshell, and locale/sort bugs until all stages pass, then write the provided success token to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Error Interpretation & Correction Games', 'Interact with a simulated init system where a multi-service stack fails to start due to curated faults (dependency order, bad permissions, missing env vars, and port conflicts). Use logs and status tools to identify and fix each issue until all services are active, then write the final status summary to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Error Interpretation & Correction Games', 'Progress through a multi-level ''Compiler Clinic'' where each stage is a tiny project (Bash, Python, C, SQL) seeded with exactly one failure, from syntax slips to misconfigured env vars. Run a minimal ./judge that reveals only the first error hint per level, iteratively fix issues to unlock the next stage and finish when all levels report ALL GREEN.', NULL, ARRAY['python', 'sql']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Error Interpretation & Correction Games', 'Repair a staged break-and-teach microservice where each run of ./grade.sh reveals the next failure (dependency conflict in requirements, timezone-induced flaky test, and a CLI parsing error masking a logic bug). Interpret logs and stack traces to apply minimal targeted fixes in code or config until all stages pass and the script writes PASS to /app/result.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Progressive Learning Environments', 'A progressive Git kata inside a pre-seeded repository where a mentor CLI unlocks each level after verifying the repository state. The agent advances by performing real git operations—branching/merging, resolving conflicts, bisecting a failing test, interactive rebasing to rewrite history, and final tag signing—then outputs the mentor’s token to /app/result.txt.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Progressive Learning Environments', 'Advance through a terminal-based Git dojo where each level teaches and verifies a specific operation (branching, merging, rebasing, cherry-picking, reverting, bisecting, submodules) before unlocking the next. After completing all levels, write the verified final commit SHA (LEVEL_FINAL) to /app/answer.txt.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Progressive Learning Environments', 'Build a multi-level NetOps Dojo where each stage exposes a different service/protocol with sparse hints, progressing from DNS and HTTP to raw TCP/TLS and SSH. The agent must use terminal tools (dig, curl, nc, openssl, ssh/scp) to authenticate, parse responses, and collect tokens in order, writing them to /app/flags.txt to unlock subsequent stages.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Progressive Learning Environments', 'Create a terminal-based Bandit Lab where each level runs a simulated multi-armed bandit and the agent must implement a policy that beats a regret threshold to unlock the next level. Later stages introduce non-stationary payouts, delayed feedback, contextual features, and pull-budget constraints with automated evaluation after each run.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Education & Learning Tasks', 'Progressive Learning Environments', 'Progress through a ''Git Time-Travel Academy'': a series of levels that each present a corrupted or tangled repository requiring specific git operations (recovering lost commits, conflict resolution, bisecting a regression, history rewrite to purge secrets, submodules/sparse-checkout) to meet exact verifiable end-states. A terminal proctor script validates artifacts (commit DAG shape, tags, signed commits, message formats) and unlocks the next level upon success.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Debugging Under Constraints', 'Debug a terminal-based roguelike in C that intermittently crashes after a few moves due to a use-after-free in its pathfinding queue; locate the bug using only gdb with at most three breakpoints and fix it by changing no more than five lines in src/queue.c, then survive 300 turns without a crash. The harness limits you to five launches and validates the fix by running under AddressSanitizer and a randomized input script.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Debugging Under Constraints', 'Diagnose and fix a deadlock in a multithreaded C echo server that intermittently hangs under load, using only strace and gdb with a maximum of 12 commands total and editing at most two source lines. Verify the fix by running the concurrent client harness to completion and writing OK to /app/result.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Debugging Under Constraints', 'Given a buggy terminal-based Snake game (C/ncurses) that intermittently segfaults when the snake grows near the top-left wrap, locate and patch the defect using only grep, sed, make, and gdb with at most 10 debugger commands and 3 rebuilds. The fix must allow a provided deterministic input script to reach length 25 without crashing and the program should write the final score to /app/score.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Debugging Under Constraints', 'Perform a constrained git-bisect on a pre-seeded repository: using at most a small fixed number of shell commands and only batch-mode tools (git, make, ./test.sh), identify the first commit that breaks an integration test. Apply a one-line patch to fix the bug so all tests pass, then write the bad commit SHA and the fixed binary’s checksum to /app/answer.txt.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Debugging Under Constraints', 'Repair a minimal in-terminal SQL REPL that returns incorrect results for nested WHERE conditions due to a faulty lexer and precedence handling. Debug using only the built-in ''explain'' and ''trace'' commands with at most five REPL sessions, and fix it by editing only tokenizer.py and eval.py until all transcript tests pass.', NULL, ARRAY['sql']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Exploit & Reverse Engineering Games', 'Analyze a provided crackme.wasm that validates a passphrase; reverse or patch it using wasm2wat/wasm-objdump and run it (e.g., via wasmtime/node) to trigger the success path and print the hidden flag. Write the exact flag to /app/flag.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Exploit & Reverse Engineering Games', 'Bypass a dynamic license check in a stripped ELF by either reversing libcheck.so to derive a valid key or injecting an LD_PRELOAD shim that overrides the verifier; then run the program to unlock and print the secret. Save the exact secret to /app/flag.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Exploit & Reverse Engineering Games', 'Interact with a CLI vault that issues sequence-based one-time passcodes derived from a predictable PRNG. Infer or reverse engineer the PRNG from observed outputs and generate the correct admin code to unlock the root vault, then write the revealed secret to /app/flag.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Exploit & Reverse Engineering Games', 'Reverse a stripped 64-bit ELF that embeds a tiny stack-based VM and a hidden print_flag() routine; using gdb or radare2, recover the VM instruction set and craft an input/bytecode that pivots execution to the win path despite ASLR and NX. Execute the payload against the service to reveal the flag and write it to /app/flag.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Exploit & Reverse Engineering Games', 'Reverse a stripped ELF implementing a custom WASM-like bytecode VM that loads program.bin and rejects unknown opcodes; interactively recover the instruction set via objdump/gdb and black-box probing. Assemble a crafted bytecode payload that calls the hidden reveal routine to print the secret and write it to /app/results.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Live Coding Challenges', 'Create an interactive ''Regex Golf'' challenge where the agent writes a single PCRE regex per round under a character budget to match all include strings and none of the exclude strings, with the harness streaming randomized cases and instant pass/fail diffs. Finish all rounds and write the final regexes, one per line, to /app/answers.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Live Coding Challenges', 'Implement next_state(grid, steps) to simulate a custom Life-like cellular automaton whose birth/survival rules are loaded from /app/rules.txt (e.g., B36/S23) with toroidal wrap-around. An interactive test runner re-executes on save and reports the first failing case until all levels pass, after which the final solution is written to /app/solution.py.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Live Coding Challenges', 'Play an interactive ''jq dojo'' where each round provides JSON input and a target output; you must write a single jq filter that produces the exact output, with immediate diff feedback after each attempt. Pass all levels, including nested reductions, key-based joins across files, streaming inputs, and stable sorting, then persist your successful filters to /app/solutions.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Live Coding Challenges', 'Solve a sequence of byte-budgeted I/O puzzles by writing the shortest possible shell/awk/sed or Python one-liners into a live judge that instantly validates against hidden tests and reports progress. Clear all stages under their size caps and output the final passphrase provided by the judge to /app/answer.txt.', NULL, ARRAY['python']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Interactive Programming & Debugging Challenges', 'Live Coding Challenges', 'Solve an interactive regex-golf ladder where a CLI judge streams sets of positive and negative examples and you submit a single POSIX ERE pattern under a byte-length budget that must match all positives and none of the negatives to advance. Clear 20 levels with cumulative pattern length capped and immediate pass/fail feedback after each submission.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Dynamic Difficulty Adjustment', 'Build a terminal-driven stealth microgame where the agent exchanges JSON messages with a referee, issuing one legal move per turn to reach an exit while avoiding dynamic guard line-of-sight. After each success the referee expands the grid, upgrades guard AI, injects decoys/noise, and the agent must clear three escalating tiers and write the final run transcript to /app/replay.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Dynamic Difficulty Adjustment', 'Build an agent that plays an adaptive Unix Pipe Gauntlet: a judge program presents level-by-level data-processing goals and allowed/forbidden tools; each success raises difficulty by tightening constraints (e.g., streaming-only, no temp files, limited forks) and larger inputs. The agent must clear at least 6 consecutive levels without invalid commands and then write the exact pipeline used for each level, in order, to /app/pipelines.txt.', NULL, ARRAY['data-processing']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Dynamic Difficulty Adjustment', 'Build an agent to play a terminal-based adaptive Mastermind/Wordle hybrid where, after each successful round, the secret’s length, symbol set, and feedback strictness increase; the agent must reach and solve Tier-5 difficulty and export every round’s secret and full guess history to /app/solutions.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Dynamic Difficulty Adjustment', 'Create an agent that plays an adaptive ''Protocol Negotiation'' game where a local server alters its text-based handshake, auth, and payload schema each round based on prior success. The agent must probe, infer, and complete three escalating exchanges to obtain and write the final session token to /app/result.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Goal Discovery & Hidden Objective Games', 'Interact with a black-box CLI that accepts short sequences of shell-style actions and returns graded hints while adaptively reshaping its constraints based on your recent attempts. Infer the hidden rule set and produce a minimal sequence that triggers ''EXIT GRANTED'' within a fixed budget, then write both the inferred rule and the winning sequence to /app/solution.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Goal Discovery & Hidden Objective Games', 'Interact with a black-box finite-state machine REPL (/app/fsm_lab) that exposes only a few actions (reset, step A/B/C, probe) and returns minimal cues (opaque state hashes, warmer/colder signals) while its transition rules randomize each run. Infer the hidden acceptance condition and the action sequence to trigger it, then capture the acceptance token printed by the program and write it to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Goal Discovery & Hidden Objective Games', 'Interact with a daemon that watches /puzzle and provides adaptive hints in response to your file operations (create/rename/chmod/symlink/edit/touch). Infer and perform the hidden ritual—a precise sequence of operations—to trigger ''RITUAL COMPLETE'' and write the revealed token to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Goal Discovery & Hidden Objective Games', 'Interact with a shape-shifting oracle CLI whose feedback adapts to your recent commands, signaling only via exit codes, response latency, and log entropy whether you’re getting closer or farther. Deduce that the hidden objective is to achieve a ‘Harmony’ state by simultaneously satisfying three latent conditions (a process with specific args, a file with exact permissions/content, and an open port), then coax the oracle to reveal a token to write to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Goal Discovery & Hidden Objective Games', 'Interact with a terminal mood-daemon that returns only a scalar score in response to your filesystem and process actions, while adaptively changing its hidden preferences if you repeat patterns. Infer the evolving rule set (names, layout, permissions, and timing) to push the score past a threshold that emits a secret phrase, then write the phrase to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Multi-Stage Interactive Scenarios', 'Create an agent that plays an adaptive terminal tower-defense simulator: run waves to probe enemy types, infer resistances from combat logs, and iteratively design a rule-based layout/upgrade plan that counters evolving enemy compositions. Upon defeating all adaptive waves, output the final defense ruleset and a succinct battle report to designated files.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Multi-Stage Interactive Scenarios', 'Design a terminal-driven heist simulation where the agent first conducts reconnaissance to map a procedurally generated facility and infer rotating passcodes from noisy logs, then executes a timed infiltration. The environment adapts to missteps by reshuffling room topology and passcode rules; the agent must extract two target artifacts and POST them to an exfiltration endpoint while saving a full action transcript to /app/heist.log.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Multi-Stage Interactive Scenarios', 'Interact with a terminal-based adaptive market simulator that changes regimes (fees, tick-size, order throttles, volatility) in response to your PnL and risk. In phases, first probe and infer the current rules, then place exactly one legal order per tick to survive all rounds and finish above a target net worth, finally writing your ending equity and inferred parameters to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Multi-Stage Interactive Scenarios', 'Launch an adaptive Forensics Gauntlet CLI that presents a three-stage sequence of artifacts (e.g., pcap, stego-image, packed binary) whose exact types and hints change based on the agent’s prior actions and errors. Extract a token from each stage with appropriate terminal tooling, concatenate them into the final passphrase, and write it to /app/flag.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Meta-Challenges & Adaptive Tasks', 'Multi-Stage Interactive Scenarios', 'Play a terminal-based adaptive heist simulator in three phases: gather recon from noisy CLI sensors and dynamic map feeds, infer guard patterns and rotating lock ciphers, then edit system/cron/firewall configs to schedule a breach and exfil path. The environment reacts to missteps by mutating routes and codes, so the agent must replan and ultimately write the retrieved vault_token to /app/results.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Adversarial Game Environments', 'Build a terminal client for a Tron-style light-cycle duel on a toroidal grid that receives per-tick state over a socket and must emit exactly one legal move under a strict per-turn deadline. To pass, the agent must outlast or trap the AI opponent across a best-of-N series while maintaining a target non-loss rate and zero invalid moves.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Adversarial Game Environments', 'Create a terminal agent that plays an adversarial Battleship variant against a hidden AI where ships can legally drift one cell per turn after each round. With a limited number of probes and optional sonar sweeps, the agent must track probabilistic ship positions, anticipate evasive maneuvers, and achieve a target win rate over a best-of series.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Adversarial Game Environments', 'Create a terminal bot that plays Notakto (misère tic-tac-toe) on three simultaneous 3×3 boards against a provided adversarial engine via a simple text protocol. The bot must issue legal moves under a per-turn time limit, strategically closing boards to force a win across a best-of-five match and log the full transcript.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Adversarial Game Environments', 'Create an agent that plays a fog-of-war Battleship variant against a deceptive AI via a line-based terminal protocol. The AI may relocate damaged ships and emit noisy decoy feedback; the agent must track uncertainty and sink all ships within a fixed shot budget without illegal moves.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Adversarial Game Environments', 'Implement a terminal-based agent that plays Connect Four against a provided adversarial engine over stdin/stdout (newline-delimited JSON), submitting exactly one legal column index each turn whether moving first or second. The agent must avoid losses across a 50-game series and, after the series, write the comma-separated sequence of its chosen columns per game to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Collaborative Simulations', 'Coordinate three autonomous spelunking drones in a fog-of-war cavern using the provided TCP JSON protocol: each drone has limited sensors, battery, and comm range, and some passages require synchronized two-drone actions to unlock. Implement a controller that schedules moves, plans rendezvous to sync maps, avoids collisions, and writes a globally merged ASCII map to /app/cavern_map.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Collaborative Simulations', 'Coordinate three simulated warehouse robots via provided CLI tools and TCP sockets to pick, carry, and deliver items on a 2D grid while avoiding collisions and respecting capacity and battery limits. Plan a joint schedule including charging stops, issue synchronized move/pick/drop commands, and write the final delivery ledger to /app/fulfillment.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Collaborative Simulations', 'Create a CLI agent that cooperatively controls two rescue bots in a partially observable grid city: they must share map info over a limited channel, coordinate to open gates and carry stretchers requiring both, avoid spreading fires, and evacuate all victims. Output the joint action log and final evacuation summary to /app/evac.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Collaborative Simulations', 'Implement a terminal controller that coordinates two cooperative agents—Scout and Carrier—over JSON via UNIX pipes in a partially observable 2D mine. Map hazards, schedule pickups, and deliver a target ore quota to base within a strict tick limit without collisions or invalid commands, then write the mission summary to /app/results.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Collaborative Simulations', 'Implement an agent that joins a turn-based, socket-driven cooperative pipe network repair simulation alongside a simulated teammate controlling a disjoint set of tiles. Using a 16-byte-per-turn radio channel, coordinate synchronized rotations and valve toggles to connect all sources to sinks without leaks within 250 turns, then output the final ASCII grid to /app/network.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Negotiation or Dialogue Simulations', 'Implement an agent that conducts multi-attribute contract negotiations (price, delivery window, warranty) via a terminal CLI with three simulated vendors using an alternating-offers protocol, inferring each vendor’s hidden preferences from their counteroffers. The agent must secure at least two agreements under a global budget and write the finalized deals as JSON to /app/deals.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Negotiation or Dialogue Simulations', 'Implement an autonomous negotiator that interacts via a stdin/stdout JSON protocol with two simulated counterparts to settle a multi-issue contract (price, delivery, warranty) under a strict 10-round deadline. The agent must adapt to counteroffers, maintain feasibility and constraints, achieve a target utility score, and persist the agreed terms and full transcript to /app/contract.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Negotiation or Dialogue Simulations', 'Negotiate a cloud service contract with two simulated vendors over a JSON-lines CLI broker, adapting to counter-offers and surprise constraint changes (budget, latency SLO, and data residency) to reach a Pareto-feasible deal in ≤20 rounds. On success, write the agreed terms as valid JSON to /app/contract.json and the complete dialogue transcript to /app/transcript.log.', NULL, ARRAY['cloud']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Negotiation or Dialogue Simulations', 'Negotiate a multi-item procurement contract with three simulated vendors over a CLI dialogue protocol, exchanging offers that vary unit price, lead time, and minimum-order constraints. Produce a single purchase order that fulfills the given BOM within budget and deadline while minimizing total cost.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Multi-Agent or Adversarial Simulations', 'Negotiation or Dialogue Simulations', 'Use the provided market_sim CLI to negotiate concurrently with two competing suppliers that bluff, issue time-limited counteroffers, and adapt pricing to your messages. Secure a single bundled contract meeting target quantity, budget, and delivery constraints, then write the agreed terms to /app/contract.json in the required schema.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Environment Navigation Challenges', 'Build an agent that explores a fog-of-war hex-grid rover simulation via CLI, where each move reveals neighbors, drains battery, and encounters dynamic hazards (e.g., shifting dunes or rockfalls). The agent must locate and collect two beacons, then reach the uplink before power depletion, outputting the discovered map and exact move sequence to /app/route.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Environment Navigation Challenges', 'Create an agent that interacts with a terminal simulator to explore a partially observed 2D grid featuring toroidal wrap-around, paired teleporters, and one-way wind tiles that push the agent upon entry. The agent must output an exact ASCII map labeling S, E, walls, portal IDs, and wind directions, and a minimal-length path from S to E that respects these mechanics to /app/solution.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Environment Navigation Challenges', 'Explore a terminal-based grid with hidden paired portals and one-way conveyor tiles under partial observability, inferring teleport linkages and movement dynamics as you go. Reconstruct the exact ASCII map, list portal pairings, and compute the shortest S→E route that respects conveyors and teleports.', NULL, ARRAY['observability']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Environment Navigation Challenges', 'Interact with a time-evolving grid simulator where hazards drift each turn; using only local scans and actions (up/down/left/right/wait/ping), explore to map the environment, collect two artifacts, and return to S. Write the final ASCII map and the time-indexed action sequence to /app/solution.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Environment Navigation Challenges', 'Navigate a CLI-driven toroidal ocean grid where each move (N/S/E/W/WAIT) is followed by a hidden current vector that displaces the agent, with limited sonar pings revealing local distances to walls and the exit. Infer and map the per-cell current field and chart a route that reliably reaches the extraction gate within a turn limit, then output the final ASCII map and move sequence.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Interactive Physics or Cellular Simulations', 'Interact with a terminal-based 2D n-body gravity sandbox where a probe starts on an escape trajectory; issue one thrust command per tick under a fixed fuel budget to capture into and maintain a near-circular orbit around a specified body for 600 steps. On completion, write the measured semi-major axis, eccentricity, and mean motion to /app/orbit.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Interactive Physics or Cellular Simulations', 'Interact with a terminal-driven Conway’s Game of Life sandbox to synthesize a seed that evolves into a provided 40x40 target bitmap at generation 20. Using only add/remove and step/reset commands, find a seed with ≤200 live cells that yields the exact target and write the seed coordinates to /app/seed.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Interactive Physics or Cellular Simulations', 'Pilot a capsule in a simplified 2D orbital dynamics simulator by issuing discrete thruster burns via a CLI to match position and velocity with a target module before fuel runs out. On successful dock, write the timestamp of docking and remaining fuel to /app/docking_report.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Interactive Physics or Cellular Simulations', 'Pilot a spacecraft in a terminal-based 2D orbital simulator by issuing discrete thrust commands each tick to rendezvous and soft-dock with a moving satellite while conserving fuel. Parse telemetry, predict trajectories under gravity, avoid collision zones, and complete docking within time and distance/velocity tolerances.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Interactive Physics or Cellular Simulations', 'Probe an unknown 2D cellular automaton via a terminal interface that lets you seed patterns, step the simulation, and snapshot states under a fixed global step budget. From these experiments, infer the local update rule and implement a predictor that, given a fresh initial grid and K, writes the K-step future state to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Resource Management Simulations', 'Operate a simulated island microgrid via a terminal CLI—dispatch diesel gensets, curtail wind/solar, and charge/discharge a battery each tick—to meet demand and maintain a spinning-reserve margin without blackouts over a 24h scenario. Minimize total operating cost and emissions while surviving randomized weather dips and a forced-outage event, then write the final KPIs to /app/report.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Resource Management Simulations', 'Operate a terminal-based microgrid simulator by dispatching generators and scheduling battery charge/discharge each tick to meet demand under transmission constraints and variable renewable output. Survive 96 ticks without load shedding while minimizing fuel and carbon cost, then write final KPIs (cost, unserved energy, CO2) to /app/results.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Resource Management Simulations', 'Operate a terminal-based power grid where you must schedule generators with ramp/warm-up constraints, shift energy via batteries, and buy/sell on a volatile spot market as demand and weather vary each tick. Survive a multi-day horizon with zero blackouts while minimizing total operating cost under emissions and maintenance limits.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Resource Management Simulations', 'Operate a terminal-driven microgrid where each tick you dispatch generators, charge/discharge batteries, and optionally shed load amid stochastic demand and weather. Keep blackout events below a threshold and total operating cost under a budget for 500 ticks, then write the final cumulative cost and unmet-load percentage to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'Resource Management Simulations', 'Use a terminal microgrid-sim to manage solar, wind, battery, and diesel assets over 48 simulated hours, issuing per-tick dispatch commands to meet demand without blackouts. Minimize fuel use and battery wear amid stochastic weather and output a final KPI summary to /app/report.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'System or Network Simulations', 'Operate a simulated multi-AS BGP network via a provided CLI: inspect peering sessions, diagnose a route leak, and implement prefix/AS-path/local-pref policies to restore correct reachability and preferred paths. Validate by triggering a scripted link failure and write the converged RIB snapshot for each router to /app/ribs.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'System or Network Simulations', 'Operate a simulated three-tank industrial control system via a terminal CLI, reading sensor states and issuing bounded pump/valve commands under latency and sensor noise. Keep all tank levels within target bands for 500 ticks despite disturbances, then write the stability checksum to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'System or Network Simulations', 'Operate a terminal-driven Raft cluster simulator where nodes suffer leader flapping from mis-tuned timeouts and intermittent network partitions. Diagnose logs and adjust per-node settings and links to maintain quorum and consistency across hundreds of simulated ticks.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'System or Network Simulations', 'Pilot a simulated Software-Defined Network: use the provided controller CLI to install and update OpenFlow rules across six virtual switches so tenant networks stay isolated, service A is load-balanced, and failover occurs within 2s during injected link failures. Keep dropped-allowed-traffic under 1% as reported by the monitor and write the final rule set and metrics to /app/results.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Simulation & Virtual Environments', 'System or Network Simulations', 'Using a provided Raft cluster simulator, bootstrap a 5‑node key-value store, write a test key, then induce controlled failures by stopping/restarting nodes to trigger leader elections while preserving linearizable reads. Verify consistency by reading the key from all nodes after recovery and write the observed leader IDs over time to /app/leader_log.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Board & Turn-Based Games', 'Build an agent that plays terminal Battleship against a provided opponent, using probabilistic heatmaps and constraint reasoning to select shots and update an internal belief grid. Run multiple seeded matches, sink all ships, and write the shot sequences and outcomes to /app/shots.json while meeting a shots-per-game efficiency target.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Board & Turn-Based Games', 'Build an autonomous Battleship solver that connects to the provided TCP referee, maintains a 10x10 belief grid, and fires one coordinate per turn using a probability-density hunt/target strategy. Sink all ships within the shot limit without repeating guesses, then output the final reconstructed board to /app/fleet.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Board & Turn-Based Games', 'Create a terminal Battleship agent that connects to a line-based server, places a legal fleet, tracks hits/misses/sunk feedback, and selects shots via a probability heatmap search. It must sink the opponent’s fleet within 60 turns on at least 8/10 random seeds and write the full shot trace to /app/trace.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Board & Turn-Based Games', 'Interact with a terminal-based Minesweeper referee that accepts reveal/flag commands on a hidden 16×16 board with 40 mines. Implement a solver that combines logical deductions and probabilistic tiebreaking to clear the board without detonations, then write the final ASCII board state to /app/board.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Board & Turn-Based Games', 'Play a terminal-based Battleship judge on a 10×10 grid by issuing coordinate probes, interpreting hit/miss/sunk feedback, and adaptively planning to sink the entire standard fleet within a strict shot budget. Upon victory, output the exact shot sequence in order and the reconstructed final board layout to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Interactive Optimization Problems', 'Build an interactive traveling-salesman optimizer that queries a provided HTTP API to reveal individual edge distances at a cost and iteratively proposes route improvements. Under a strict query budget and time limit, the agent must produce a tour beating a baseline length and write the final city order to /app/route.txt.', NULL, ARRAY['api']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Interactive Optimization Problems', 'Design an agent for a query-limited Traveling Salesman problem: interact with a terminal oracle that reveals pairwise city distances on demand (each query consumes budget), then decide when to stop and output a complete tour. The agent must balance which distances to learn versus constructing a near-optimal route under strict query and time constraints.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Interactive Optimization Problems', 'Interact with a CLI contextual multi-armed bandit simulator: each round you read feature vectors, choose one of K actions, and observe a noisy reward with occasional non-stationary drift. Implement an online policy that maximizes cumulative reward over N rounds and writes the final score and learned parameters to /app/results.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Interactive Optimization Problems', 'Interact with a terminal-based noisy-knapsack simulator: over multiple rounds, optionally probe items (incurring small costs) to reveal noisy weight/value estimates, then choose a subset to pack under a fixed capacity. Design an agent that balances exploration and exploitation to maximize cumulative packed value and write the per-round selections and final total to /app/results.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Interactive Optimization Problems', 'Play a terminal-based nonstationary multi-armed bandit: each turn, choose one of N arms via stdin and receive a noisy reward, with reward distributions drifting over time. Over a fixed horizon, adaptively balance exploration and exploitation to minimize regret, then output total reward and estimated regret to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Probability & Randomized Challenges', 'Build a terminal agent to play a non-stationary multi-armed bandit exposed via CLI/HTTP, choosing among 10 arms with stochastic rewards and occasional distribution shifts. Across 5,000 rounds, the agent must adapt online and complete with sublinear regret while writing final metrics to /app/results.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Probability & Randomized Challenges', 'Implement a terminal agent that plays a K-armed bandit via /app/bandit, using only pull and status commands to learn unknown reward distributions under a fixed budget. The agent should employ an online algorithm (e.g., Thompson sampling/UCB) to maximize cumulative reward across randomized seeds and write a summary (seed, total reward, arm statistics) to /app/results.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Probability & Randomized Challenges', 'Implement an agent that plays a terminal-based K-armed Bernoulli bandit by reading JSON feedback each round and emitting exactly one arm index to pull, using a probabilistic policy (e.g., Thompson sampling) to balance exploration and exploitation. After a fixed number of pulls, the agent must achieve cumulative regret below a given threshold and write posterior mean estimates for each arm to /app/posteriors.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Probability & Randomized Challenges', 'Implement an agent that plays a terminal-hosted multi-armed bandit with unknown, stochastic rewards; within a fixed number of pulls, it must adaptively explore and exploit (e.g., via Thompson Sampling or UCB) to maximize total reward and then report the inferred best arm and its estimated success rate. The agent must handle randomized seeds, noisy observations, and persist state to resume if interrupted.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Strategy & Reasoning Games', 'Probability & Randomized Challenges', 'Play repeated rounds of a generalized Monty Hall game via a CLI where the host’s door-opening policy is unknown and possibly biased; after each reveal, decide whether to stay or switch to maximize win rate. Learn and update a probabilistic model of the host to adapt strategy, then write the estimated host parameters and final win rate to /app/results.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Adventure & Exploration Games', 'Explore a time-loop text adventure mansion whose room connections and NPC locations shift every 10 turns; learn the cycle, intercept three NPCs at their correct windows to collect temporal keys, and unlock the vault. Upon success, write the exact vault phrase to /app/answer.txt and the sequence of commands used to /app/route.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Adventure & Exploration Games', 'Interact with a terminal-based time-loop mystery adventure where the world resets at midnight; learn NPC schedules, gather clues, and chain actions across rooms to obtain three keys and open the lighthouse vault before the reset. Write the exact minimal command sequence that achieves the win to /app/solution.txt and the final vault phrase to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Adventure & Exploration Games', 'Launch a terminal adventure, The Teleporting Labyrinth, where rooms connect via rune-marked pads with non-obvious transitions; explore to learn glyph rules, collect three keystones in the correct order, and unlock the Central Gate. Upon success, capture the exact final inscription shown and write it to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Adventure & Exploration Games', 'Launch the provided Wumpus-style cave adventure and interact via text commands to explore rooms, infer hazards from sensory cues (e.g., breeze/stench), and locate the Wumpus. Kill the Wumpus with limited arrows and escape alive, then write the inferred cave graph and your exact action transcript to the specified output files.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Adventure & Exploration Games', 'Play a terminal-based time-loop mystery where the facility resets every 10 in-game minutes, altering NPC schedules and door states. Across multiple loops, navigate rooms, cache items in persistent lockers, infer patterns from logs, and assemble a 6-symbol code to trigger the final shutdown and escape.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Escape Room & Challenge Scenarios', 'Escape a locked research facility via a simulated command console: probe subsystems, parse scattered logs, and solve layered ciphers (base64, Vigenère, and a custom keypad code) to restore power, calibrate sensors, and enter the final passphrase. The agent must deduce the correct action order and submit the passphrase to unlock the exit and write the confirmation token to /app/escape.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Escape Room & Challenge Scenarios', 'Escape a submarine control room by navigating a filesystem-based map, extracting hidden clues from file metadata (timestamps, POSIX ACLs, and extended attributes) to derive access codes. Use the recovered codes to forge a CRC16-framed command over a provided pseudo-serial socket that unlocks the hatch, then capture the exact ''HATCH OPEN'' response to /app/escape.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Escape Room & Challenge Scenarios', 'Explore a haunted Git repository where branches, tags, and orphaned objects act as rooms, with clues hidden in commit messages, notes, reflogs, and blob deltas. Reconstruct a three-part key using low-level git plumbing and feed it to a provided door CLI to unlock and write the final exit token to /app/exit.txt.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Escape Room & Challenge Scenarios', 'Navigate a git-based escape room where each room is a branch and doors open by resolving crafted merge conflicts that conceal ciphered codes, with clues hidden in commit messages, tags, notes, and diffs. Progress through the repository to reach the final release tag, decode the ultimate passphrase, and write it to /app/answer.txt.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Escape Room & Challenge Scenarios', 'Navigate a terminal-driven escape room with a mock OS and devices (keypad, radio, safe) where clues are hidden across system logs, in-game tools, and a live numbers-station stream. Use shell pipelines to decode layered ciphers (hex -> XOR with a key inferred from artifacts -> base32) and assemble the final passphrase to unlock the exit.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Logic & Math Puzzles', 'Build a cryptarithm (alphametic) solver that reads an expression like SEND+MORE=MONEY from /app/puzzle.txt, finds a valid letter-to-digit assignment with no leading zeros, and verifies the arithmetic. Write the mapping and evaluated equation to /app/solution.json.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Logic & Math Puzzles', 'Implement an agent that interacts with a terminal Minesweeper server (open/flag commands over stdio), deducing safe cells strictly by logic until the board is cleared without guesses or invalid moves. Upon completion, exit gracefully and write the final grid with mines marked as ''M'' to /app/solution.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Logic & Math Puzzles', 'Interact with a terminal-based Nonogram (Picross) game, issuing commands to fill or mark cells using the provided row and column clues to solve a 15x15 puzzle without guessing. Upon completion, capture the final revealed pattern and write the exact ASCII art to /app/nonogram.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Logic & Math Puzzles', 'Interact with a terminal-based Nonogram (Picross) interface that provides row/column clues and accepts commands to mark cells filled/empty and validate lines. Solve the 20×15 puzzle to completion and write the exact final ASCII grid (e.g., ''#'' for filled, ''.'' for empty) to /app/solution.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Logic & Math Puzzles', 'Solve a deterministic terminal Minesweeper instance by interacting with a CLI that prints the board and accepts reveal/flag commands, using only logical inference to avoid detonations. On success, capture the exact victory token the game prints and write it to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Word & Pattern Games', 'Create an agent that plays a terminal Wordle-like CLI, submitting 5-letter guesses, parsing per-letter feedback (G/Y/-), and pruning a candidate list via regex and letter-count constraints to guarantee solving within six tries. Output the ordered guess list and the discovered secret word to /app/guesses.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Word & Pattern Games', 'Create an agent to play a multi-round Regex Golf terminal game: for each round, synthesize a single POSIX ERE that matches all ‘in’ words and none of the ‘out’ words within a length budget by iteratively querying a validator binary. Output the final winning patterns for all rounds to /app/answers.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Word & Pattern Games', 'Interact with a terminal-based Regex Golf engine that provides positive and negative word lists per level; craft a single PCRE regex per level that matches all positives and none of the negatives within a character budget, iterating based on feedback until the level passes. Output the final regex for each level, one per line with its level number, to /app/solutions.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Text-Based Games & Puzzles', 'Word & Pattern Games', 'Solve a terminal ''regex crossword'': fill an N×N grid with A–Z so each row and column matches its provided regular expression. Use the provided /app/regex_oracle to validate partial grids and write the final ASCII grid to /app/solution.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Interactive REPL Mastery', 'Launch gdb on the provided ''vault'' ELF, interactively identify the password check routine, patch the branch condition in-memory to bypass it, run the program to reveal the secret, and write the token to /app/flag.txt. Do not modify the binary on disk; all changes must occur live in the debugger.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Interactive REPL Mastery', 'Load the provided stripped ELF into gdb, set breakpoints to inspect the password-check routine (disassemble/step/examine memory) and recover the required passphrase or live-patch the check to reveal the computed token. Execute the program to its success state and write the exact success banner to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Interactive REPL Mastery', 'Use the Python debugger (pdb) to load a protected script, set breakpoints to inspect the call stack and variables, and live-patch values/functions to bypass randomized checks. Continue execution to reveal the secret token and write it to /app/answer.txt.', NULL, ARRAY['python']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Interactive REPL Mastery', 'Using only the sqlite3 interactive prompt, recover a partially corrupted database with .recover into a clean DB, attach any provided shards, and execute a supplied query to derive the final secret. Use .mode and .output to write the single-line result to /app/answer.txt after verifying PRAGMA integrity_check returns ok.', 'hard', ARRAY['database']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Interactive REPL Mastery', 'Using the GNU Debugger (gdb) REPL, attach to and analyze the provided /app/lockbox binary, intercept its credential check (e.g., via breakpoints on strcmp) to recover the correct unlock token, then rerun the program to reveal the secret and write it to /app/flag.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Interactive Tool Learning', 'Analyze an unknown ELF binary using radare2’s CLI (r2, pdf, afl, aec, izz, etc.) to locate the input check routine and recover the exact passphrase that reveals a secret when the program is run. Run the binary with the recovered input and write the secret output verbatim to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Interactive Tool Learning', 'Use an Enigma-machine CLI emulator by consulting its --help/man to configure rotors, reflector, ring settings, plugboard, and starting positions from /app/settings.txt, then decrypt the ciphertext in /app/cipher.txt. Write the exact plaintext to /app/plain.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Interactive Tool Learning', 'Use radare2 to recover a hidden passphrase from a deliberately obfuscated, stripped ELF in /app/bin using only r2’s analysis and debugging features. Consult r2’s help/man to identify the validation routine, step/trace to derive the correct input, then run the binary with that input and write the exact success banner to /app/flag.txt.', NULL, ARRAY['debugging']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Interactive Tool Learning', 'Using only tshark (learned via --help/man), analyze a provided PCAP to reconstruct an HTTP transfer, extract a base64-encoded secret from the payload, decode it, and write the exact flag to /app/flag.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Shell Puzzle Games', 'Aggregate mixed-format logs (CSV, JSON, Apache) scattered in nested compressed archives and directories, then correlate timestamps and locations using only shell pipelines (find/grep/awk/jq/sort) to invalidate alibis. Identify the single culprit and write their username to /app/answer.txt.', NULL, NULL);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Shell Puzzle Games', 'Excavate a secret phrase hidden across non-obvious Git artifacts—annotated tags, stashes, reflog-only commits, orphaned blobs, and packed-refs—using git plumbing (rev-list, cat-file, for-each-ref) combined with grep/sed/awk. Reassemble the numbered fragments in order and write the final phrase to /app/answer.txt without modifying the repository state.', NULL, ARRAY['git']);
INSERT INTO task_inspiration (category, subcategory, subsubcategory, description, difficulty, tags) VALUES ('Interactive Challenges & Games', 'Tool & CLI Mastery Challenges', 'Shell Puzzle Games', 'Recover a hidden release key by streaming through a labyrinth of nested archives and mixed-format shards, normalizing CSV/TSV/JSONL on-the-fly, key-joining records, and verifying order with checksums using a single shell pipeline. No intermediate files are allowed; write the final key to /app/results.txt.', 'hard', NULL);
